const validation = (userData) => {
    const errors={}

    if(!/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(userData.email)){
        errors.email = 'el Email ingresado no es valido mi diosa uwu'
    }
    if(!userData.email){
        errors.email = 'Ingresa un Email capo'
    }
}

export default validation
